sys_variable_value.value:
(function execute(inputs, outputs) {
	var json = JSON.parse(inputs.responseBody);

  	var diskGb = json.specs.disk;
  	var memoryGb = json.specs.memory;
  	outputs.disk_capacity_in_gb = diskGb;
  	outputs.ram_in_gb = memoryGb;


})(inputs, outputs);

