sys_variable_value.value:
(function execute(inputs, outputs) {

  var types = [];
  var payload = JSON.parse(inputs.responseBody);
  for(var i=0; i<payload.data.length; i++){
    var linodeType = payload.data[i];
    var type = { "label": linodeType.label,
                  "name":linodeType.id
                 };
    types.push(type);
  }

  outputs.type = { data: types }; // final properly formatted output

})(inputs, outputs);

