sys_variable_value.value:
(function execute(inputs, outputs) {
var json = JSON.parse(inputs.payload);
 outputs.label = json.label;
  outputs.ipv4 = json.ipv4[0];
  outputs.id = json.id;

})(inputs, outputs);

