sys_variable_value.value:
(function execute(inputs, outputs) {
  var result = true;
  var message = "";
	if (inputs.status != 200) {
      result = false;
      message = JSON.parse(inputs.responseBody).reason;
    }

  outputs.result = result;
  outputs.message = message;


})(inputs, outputs);

