sys_variable_value.value:
(function execute(inputs, outputs) {
 var vms = [];
  var payload = JSON.parse(inputs.responseBody);
  for(var i=0; i<payload.data.length; i++){
    var linodeVM = payload.data[i];
    var vm = {'label': linodeVM.label + " " + linodeVM.type + " " + linodeVM.region + " " + linodeVM.image,
                     'name':linodeVM.id}; 
      vms.push(vm);

  }

  vms.sort(function(a, b){ return a.label.toUpperCase() < b.label.toUpperCase() ? -1: 1 });
  gs.info("After sort, vms is "+JSON.stringify(vms) );

  outputs.vm = { data: vms }; // final properly formatted output               


})(inputs, outputs);

