sys_variable_value.value:
(function execute(inputs, outputs) {
  var payload = JSON.parse(inputs.responseBody);
  var result = true;
  var message = "";
	if (inputs.status != '200') {
      result = false;
      for (var i = 0; i < payload.errors.length; i++) {             
        message = payload.errors[i].reason + "\n";

      }
    }
  outputs.result = result;
  outputs.message = message;
})(inputs, outputs);

